import React, { useState } from 'react';
import { Alert, View, ScrollView, Text, Image, Button, StyleSheet, TextInput } from 'react-native';


export default function Alterar() {
  const [cdEscolhido, setCdEscolhido] = useState(null);
  const [nome, setNome] = useState(null)
  const [compositor, setCompositor] = useState(null)


  const deletarCd = () => {
    const endpoint = `https://Toca do Vinil1.pythonanywhere.com/nalinha/delete/${nome}/${compositor}/${latitude}/${longitude}/${potencialhidrogenico}/${oxigeniodissolvido}/${temperatura}/${condutividade}/${salinidade}/${turbidez}`;
    console.log(endpoint)
    console.log(nome)
    fetch(endpoint)
      .then(resposta => resposta.json())
        .catch(() => {
          Alert.alert('Exclusão', 'Cd deletado com sucesso!');
        });
  }


  const getcd = ( nome) => {
    const endpoint = `https://Toca do Vinil1.pythonanywhere.com/leitura/${ nome}`;
    fetch(endpoint)
      .then(resposta => resposta.json())
      .then(json => {
        const cd = {
          nome: json.cd,
          compositor: json.compositor,
          condutividade: json.condutividade,
          latitude: json.latitude,
          longitude: json.longitude,
          oxigeniodissolvido: json.oxigeniodissolvido,
          potencialhidrogenico: json.potencialhidrogenico,
          salinidade: json.salinidade,
          temperatura: json.temperatura,
          turbidez: json.turbidez
        };
        setCdEscolhido(cd);
      })
      .catch(() => {
        Alert.alert('Erro', 'Não foi possível encontrar este cd');
      });
  }

  return (
    <View style={styles.container}>
      <ScrollView>
        <View style={styles.topo}>
          <Text style={styles.topoTitulo}>Toca do Vinil</Text>
        </View>
        {cdEscolhido != null && (
          <View style={styles.Box}>
            <Text style={styles.linha}>Nome do cd: {cdEscolhido.nome}</Text>
            <Text style={styles.linha}>Compositor: {cdEscolhido.compositor}</Text>
          </View>
        )}


        <View style={styles.cardContainer}>
          {cdEscolhido == null && (
            <><><Text>Digite o nome do cd no campo abaixo:</Text></><TextInput style={styles.TextInputt}
              onChangeText={setNome}
              value={nome}
              KeyboardType="text" /><Button title="Selecionar um cd para exclusão" onPress={() => getcd(nome)} /></>
          )}

          {cdEscolhido != null && (
            <><><Text>Confira o cd que vai excluir e confirme no botão abaixo</Text></>
            <Button  title="Excluir" onPress={() => deletarCd()}></Button></>
          )}

        </View>

      </ScrollView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: { flex: 1, backgroundColor: '#fff' },

  topo: { height: 80, padding: 20, paddingTop: 40, marginBottom: 20, backgroundColor: '#00008B' },
  topoTitulo: { fontSize: 22, marginBottom: -10, color: '#fff', textAlign: 'center' },

  cardContainer: { borderWidth: 1, borderColor: '#d5d5d5', borderRadius: 4, marginBottom: 10, marginHorizontal: 20, padding: 10 },
  cardTitle: { fontSize: 22, marginBottom: 20, textAlign: 'center', color: '#656565' },

  Box: { alignItems: 'center' },
  linha: { fontSize: 18 },
  TextInputt:{
    backgroundColor: '#DCDCDC',
    borderRadius: 4,
    marginBottom: 15,
    marginTop: 5,
    paddingLeft: 10,
    color: '#4F4F4F'
},
});
